import osfrom fastapi import FastAPI, UploadFile, Filefrom fastapi.responses import HTMLResponsefrom docx import Documentimport fitz  # PyMuPDFimport openaiapp = FastAPI(docs_url=None, redoc_url=None, openapi_url=None)# Fetch OpenAI API Key from Render Environment VariableOPENAI_API_KEY = os.getenv("OPENAI_API_KEY")@app.get("/", response_class=HTMLResponse)async def home():    return """    <html>    <head>        <title>SCN REPLY</title>        <style>            body {                display: flex;                flex-direction: column;                justify-content: center;                align-items: center;                height: 100vh;                background-color: white;                margin: 0;                padding-top: 50px;            }            h1 {                color: #00008B;                font-size: 3rem;                text-align: center;            }            button {                background-color: #00008B;                color: white;                padding: 10px 20px;                font-size: 16px;                border: none;                cursor: pointer;                margin-top: 20px;                border-radius: 5px;            }            #analysis, #replyLetter {                text-align: left;                font-size: 18px;                color: black;                max-width: 80%;                margin-top: 20px;            }            input {                margin-top: 10px;            }            p {                font-size: 18px;                color: green;            }            #summaryButton, #replyButton {                display: none; /* Initially hidden */            }        </style>    </head>    <body>        <h1>SCN REPLY</h1>        <form id="uploadForm" action="/upload/" method="post" enctype="multipart/form-data">            <input type="file" name="file" accept=".pdf" required>            <button type="submit">Upload</button>        </form>        <p id="message"></p>        <button id="summaryButton" onclick="fetchAnalysis()">Summary</button>        <button id="replyButton" onclick="fetchReply()">Reply</button>          <div id="analysis"></div>        <div id="replyLetter"></div>        <script>            document.querySelector("#uploadForm").onsubmit = async (e) => {                e.preventDefault();                const formData = new FormData(e.target);                document.getElementById("message").innerHTML = "Uploading...";                const response = await fetch("/upload/", {                    method: "POST",                    body: formData                });                const result = await response.json();                document.getElementById("message").innerHTML = "File uploaded successfully!";                document.getElementById("summaryButton").style.display = "block"; // Show Summary button                document.getElementById("replyButton").style.display = "block"; // Show Reply button            };            async function fetchAnalysis() {                document.getElementById("summaryButton").innerHTML = "Generating Summary...";                const response = await fetch("/get_analysis");                const result = await response.json();                                let analysisHtml = "";                result.analysis.forEach((point, index) => {                    analysisHtml += `<p><b>${index + 1}.</b> ${point}</p>`;                });                document.getElementById("analysis").innerHTML = analysisHtml;                document.getElementById("summaryButton").innerHTML = "Summary";            }            async function fetchReply() {                document.getElementById("replyButton").innerHTML = "Generating Reply...";                const response = await fetch("/get_reply");                const result = await response.json();                document.getElementById("replyLetter").innerHTML = `<pre>${result.reply}</pre>`;                document.getElementById("replyButton").innerHTML = "Reply";            }        </script>    </body>    </html>    """def extract_text_from_pdf(pdf_path):    """ Extract text from PDF using PyMuPDF (fitz). """    doc = fitz.open(pdf_path)    text = ""    for page in doc:        text += page.get_text("text") + "\n"    return textdef analyze_text_with_chatgpt(text, task="analysis"):    """ Send extracted text to OpenAI ChatGPT for detailed analysis or reply letter generation. """    api_key = os.getenv("OPENAI_API_KEY")    if not api_key:        print("Error: OpenAI API key is missing. Please configure it in Render.")        return ["Error: OpenAI API key is missing. Please configure it in Render."]    try:        client = openai.OpenAI(api_key=api_key)        if task == "analysis":            messages = [                {"role": "system", "content": "You are an AI that analyzes PDFs and provides a detailed report in numbered points."},                {"role": "user", "content": f"Analyze this document and provide a detailed breakdown with correct numbering (1, 2, 3, ...):\n{text}"}            ]        else:  # Task is "reply"            messages = [                {"role": "system", "content": "You are an AI that drafts formal reply letters based on given document analysis."},                {"role": "user", "content": f"Draft a professional reply letter based on the following analysis:\n{text}"}            ]        response = client.chat.completions.create(            model="gpt-4",            messages=messages        )        return response.choices[0].message.content    except openai.OpenAIError as e:        print(f"OpenAI API Error: {e}")        return f"Error: {e}"# Store data in memory temporarilyuploaded_data = {}@app.post("/upload/")async def upload_file(file: UploadFile = File(...)):    pdf_path = f"temp_{file.filename}"        # Save the uploaded file    with open(pdf_path, "wb") as buffer:        buffer.write(file.file.read())    # Extract text from the PDF    extracted_text = extract_text_from_pdf(pdf_path)    # Send text to ChatGPT for analysis    analysis_result = analyze_text_with_chatgpt(extracted_text, task="analysis")    # Send text to ChatGPT for reply generation    reply_result = analyze_text_with_chatgpt(analysis_result, task="reply")    # Remove the temporary PDF file    os.remove(pdf_path)    # Store results in memory    uploaded_data["analysis"] = analysis_result.split("\n")    uploaded_data["reply"] = reply_result    return {"message": "Upload successful"}@app.get("/get_analysis")async def get_analysis():    return {"analysis": uploaded_data.get("analysis", ["No analysis available."])}@app.get("/get_reply")async def get_reply():    return {"reply": uploaded_data.get("reply", "No reply available.")}