from fastapi import FastAPI, UploadFile, Filefrom fastapi.responses import HTMLResponse, FileResponsefrom docx import Documentimport osapp = FastAPI(docs_url=None, redoc_url=None, openapi_url=None)@app.get("/", response_class=HTMLResponse)async def home():    return """    <html>    <head>        <title>SCN REPLY</title>        <style>            body {                display: flex;                flex-direction: column;                justify-content: center;                align-items: center;                height: 100vh;                background-color: white;                margin: 0;                padding-top: 50px;            }            h1 {                color: #00008B;                font-size: 3rem;                text-align: center;            }            button {                background-color: #00008B;                color: white;                padding: 10px 20px;                font-size: 16px;                border: none;                cursor: pointer;                margin-top: 20px;                border-radius: 5px;                display: none; /* Initially hidden */            }            input {                margin-top: 10px;            }        </style>    </head>    <body>        <h1>SCN REPLY</h1>        <form id="uploadForm" action="/upload/" method="post" enctype="multipart/form-data">            <input type="file" name="file" accept=".pdf" required>            <button type="submit">Upload</button>        </form>        <br>        <button id="downloadButton" onclick="window.location.href='/download/'">Download Word File</button>        <script>            document.querySelector("#uploadForm").onsubmit = async (e) => {                e.preventDefault();                const formData = new FormData(e.target);                const response = await fetch("/upload/", {                    method: "POST",                    body: formData                });                const result = await response.text();                document.getElementById("downloadButton").style.display = "block"; // Show Download button            };        </script>    </body>    </html>    """@app.post("/upload/")async def upload_file(file: UploadFile = File(...)):    return "File uploaded successfully!"@app.get("/download/")async def download_file():    doc = Document()    doc.save("Blank_Document.docx")  # Save an empty Word file    return FileResponse("Blank_Document.docx", filename="Blank_Document.docx", media_type="application/vnd.openxmlformats-officedocument.wordprocessingml.document")